        required
      />
      <input
        value={email}
        onChange={e => setEmail(e.target.value)}
        placeholder="Email address"
        type="email"
        name="email"
        class="form-field-two"
        required
      />
      <textarea
        value={message}
        onChange={e => setMessage(e.target.value)}
        placeholder="Message"
        type="text"
        name="message"
        class="form-field-three"
        required
      />
      <button class="submit-button" type="submit">Submit</button>
    </form>
   </div>
  </>
  );
}

export default App;
