//       event.preventDefault();
//       handleSubmit(); // this won't be triggered
//     }
// };


  return (
   <>
    <img className="logo" src={'./images/logo.jpg'} alt="logo"/>
    <div class="form-container">
     <form class="form-register" onSubmit={handleSubmit}>
        {submitted && valid ? <div class="message">Thank you! Message received.</div> : true}
      <input
        value={values.fullName}
        // onChange={e => setFullName(e.target.value)}
        onChange={handleFullNameInputChange}
        placeholder="Full name"
        type="text"
        name="full Name"
        class="form-field-one"
        required
      />
      {/* {submitted && !values.fullName ? <span>Please enter your name</span> : true} */}
      <input
        value={email}
        onChange={e => setEmail(e.target.value)}
        placeholder="Email address"
        type="email"
        name="email"
        class="form-field-two"
        required
      />
      <textarea
        value={values.message}
        // onChange={e => setMessage(e.target.value)}
        onChange={handleMessageInputChange}
        placeholder="Message"
        type="text"
        name="message"
        class="form-field-three"
        ref={register({
            minLength: {
              value: 20,
              message: "Please enter 20 characters minimum"
            }
          })}

      />
      {/* <p class="character-count">{count}</p> */}
      {/* {submitted && !values.message ? <span>Please enter 20 characters minimum</span> : true} */}
