import React, { useState } from 'react';
import './index.css';

function App() { 
  const [values, setValues] = useState ({
    fullName: "",
    message: "",
  });

  const [email, setEmail] = useState("");

  const [submitted, setSubmitted] = useState(false);
  const [valid, setValid] = useState(false);


  const handleFullNameInputChange = (event) => {
    setValues({...values, fullName: event.target.value})
  }

  const handleMessageInputChange = (event) => {
    setValues({...values, message: event.target.value});
  }

  const handleSubmit = (event) => {
    // event.preventDefault();
    if(values.fullName && email && values.message) {
      setValid(true);
    }
    setSubmitted(true);
  }

//   const handleKeypress = (event) => {
//   if (event.keyCode === 13) {
//     handleSubmit();
//     }
//   }

//   const handleUserKeyPress = event => {
//     if (event.key === "Enter" && !event.shiftKey) {
//       event.preventDefault();
//       handleSubmit(); // this won't be triggered
//     }
// };


  return (
   <>
    <img className="logo" src={'./images/logo.jpg'} alt="logo"/>
    <div class="form-container">
     <form class="form-register" onSubmit={handleSubmit}>
        {submitted && valid ? <div class="message">Thank you! Message received.</div> : true}
      <input
        value={values.fullName}
        onChange={handleFullNameInputChange}
        placeholder="Full name"
        type="text"
        name="full Name"
        class="form-field-one"
        required
      />
      <input
        value={email}
        onChange={e => setEmail(e.target.value)}
        placeholder="Email address"
        type="email"
        name="email"
        class="form-field-two"
        required
      />
      <textarea
        value={values.message}
        onChange={handleMessageInputChange}
        placeholder="Message"
        type="text"
        name="message"
        class="form-field-three"
        required
      />
        <button class="submit-button" type="submit">
          Submit
        </button>
      </form>
     </div>
  </>
  );
}
export default App;
