import React, { useState } from 'react';
import './index.css';

function App() { 
  const [values, setValues] = useState ({
    fullName: "",
    email: "",
    notes: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [valid, setValid] = useState(false);

  const [ register ] = useState();

  const handleFullNameInputChange = (event) => {
    setValues({...values, fullName: event.target.value})
  }

  const handleEmailInputChange = (event) => {
    setValues({...values, email: event.target.value})
  }

  const handleNotesInputChange = (event) => {
    setValues({...values, notes: event.target.value})
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    if(values.fullName && values.email && values.notes) {
      setValid(true);
    }
    setSubmitted(true);
  }

  return (
   <>
    <img className="logo" src={'./images/logo.jpg'} alt="logo"/>
    <div class="form-container">
      <form class="form-register" onSubmit={handleSubmit}>
        {submitted && valid ? <div class="message">Thank you! Message received.</div> : true}
        <input
          onChange={handleFullNameInputChange}
          value={values.fullName}
          id="full-name"
          class="form-field-one"
          type="text"
          placeholder="Full Name"
          name="fullName"/>
        {submitted && !values.fullName ? <span>Please enter your name</span> : true}
        <input
          onChange={handleEmailInputChange}
          value={values.email}
          id="email"
          class="form-field-two"
          type="text"
          placeholder="Email"
          name="email"
        />
        {submitted && !values.email ? <span>Please enter your Email.</span> : true}
        <textarea
          onChange={handleNotesInputChange}
          values={values.notes}
          class="form-field-three"
          type="text"
          placeholder="Please enter 20 characters minimum."
          ref={register ({ required: "Notes Required", minLength: {value: 20, message: "Please enter 20 characters minimum"}})}
        />
        {submitted && !values.notes ? <span>Please enter 20 characters minimum</span> : true}
        <button class="submit-button" type="submit">
          Submit
        </button>
      </form>
    </div>
   </>
  );
}

export default App;
